function foo(n:int) {
  var result = {};
  for(var index=0; index<n; index++) {{
    var j = index * index;
    result[index] = j;
  }}
  return result;
}
// TS --> JS
function foo(n) {
  var result = {};
  for(var index=0; index<n; index++) {(function(){
    var j = index * index;
    result[index] = j;
  })();}
  return result;
}

